class NewsArt {
  String imgUrl;
  String newsHead;
  String newsDes;
  String newsCnt;
  String newsUrl;

  NewsArt({

    required this.newsUrl,
    required this.newsCnt,
    required this.newsDes,
    required this.newsHead,
    required this.imgUrl,


  });

   static NewsArt fromAPItoApp(Map<String,dynamic>article) {
    return NewsArt(
        newsUrl: article["urlToImage"]?? "data:image/jpeg;base64,",
        newsCnt: article["content"]?? "__",
        newsDes: article["description"]?? "__",
        newsHead: article["title"]?? "__",
        imgUrl: article["url"]?? "https://news.google.com/home?hl=en-IN&ceid=IN:en&gl=IN");
  }

}